{
     sh 'mvn clean package'
     junit '**/*.xml'


}

}


stage('Docker Stage')
{
     sh 'docker build -t deepthimogaparthi1207/calculatorrepo:0.${BUILD_NUMBER} .'

     withCredentials([string(credentialsId: 'docker-trial', variable: 'dockerHubpwd')]) {
          sh "docker login -u deepthimogaparthi1207 -p ${dockerHubpwd}"
            }
        sh 'docker push deepthimogaparthi1207/calculatorrepo:0.${BUILD_NUMBER}'
}
stage ('deploy to QA and Dev')
{   

   script {
      Conatiner_Size = sh(returnStdout: true, script: 'docker ps -aq')
    }
     if (Conatiner_Size)
     { sh 'docker stop $(docker ps -aq)'
     sh 'docker rm $(docker ps -aq)'
     sh 'docker run -p 8888:8080 -d --name=calculatorrepoDEV deepthimogaparthi1207/calculatorrepo:0.${BUILD_NUMBER}'
      sh 'docker run -p 8090:8080 -d --name=calculatorrepoQA deepthimogaparthi1207/calculatorrepo:0.${BUILD_NUMBER}'

        }else
        {
             sh 'docker run -p 8888:8080 -d --name=calculatorrepoDEV deepthimogaparthi1207/calculatorrepo:0.${BUILD_NUMBER}'
             sh 'docker run -p 8090:8080 -d --name=calculatorrepoQA deepthimogaparthi1207/calculatorrepo:0.${BUILD_NUMBER}'

        }

     echo 'docker ps -aq'
     sh 'docker stop $(docker ps -aq)'
     sh 'docker rm $(docker ps -aq)'
     sh 'docker run -p 8888:8080 -d --name=calculatorrepoDEV deepthimogaparthi1207/calculatorrepo:0.${BUILD_NUMBER}'
